import React from 'react'
import Header from './Header'
import { useState, useEffect } from 'react'
import Axios from 'axios'
import '../../public/<i class="far fa-trash-alt"></i>'

function Posts() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')

    useEffect(() => {
        fetch("http://localhost:3003/posts")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )

    }, [])
    const submitHandler = (e) => {
        e.preventDefault()
        Axios.post('http://localhost:3006/addposts', {
            title: title,
            body: body
        }).then((response) => {
            setItems(response);
        });
        console.log(title, body)
        // clearForm()
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <>
                <Header />
                <div style={{ textAlign: 'center' }}>
                    <form onSubmit={submitHandler}>
                        <label for="title">Enter Title:</label><br />
                        <input type="text" onChange={(e) => setTitle(e.target.value)} id="title" name="title" /><br />
                        <label for="body">Enter Description:</label><br />
                        <input type="text" onChange={(e) => setBody(e.target.value)} id="body" name="body" /><br />
                        <button type="submit">Submit</button>
                    </form>
                </div>
                <ul>
                    {items.map(item => (
                        <li key={item.id}>
                            <ul>
                                <li>TITLE :- {item.title}</li>
                                <li>DESCRIPTION :- {item.body}</li>
                                <button><i class="far fa-trash-alt"></i></button>
                                <button>edit</button>
                            </ul>
                        </li>
                    ))}
                </ul>
                {/* {items.map(item => (
                    <table key={item.id}>
                        <tr>
                            <th>TITLE :-</th>
                            <th>DESCRIPTION :-</th>
                        </tr>
                        <tr>
                            <td>{item.title}</td>
                            <td>{item.body}</td>
                        </tr>
                    </table>
                ))} */}
            </>
        );
    }
}
export default Posts
