import React from 'react'
import Header from './Header'
import { useState, useEffect } from 'react'
import Axios from 'axios'
// import { useForm } from 'react-hook-form'

function Posts() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [isEdit, setIsEdit] = useState(false);
    const [editId, setEditId] = useState('')
    const [items, setItems] = useState([]);
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [valid, setValid] = useState(true)
    // const [titleErr, setTitleErr] = useState({})
    // const [bodyErr, setBodyErr] = useState({})
    const [err, setErr] = useState({
        titleErr: '',
        bodyErr: ''
    })

    useEffect(() => {
        getData();

    }, [])
    const getData = () => {
        fetch("http://localhost:3001/posts")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }

    const submitHandler = (e) => {
        e.preventDefault()
        formValidation();
        // console.log("i submit", isValid);
        if (valid) {
            if (isEdit) {
                Axios.put(`http://localhost:3001/posts/${editId}`, {
                    title: title,
                    body: body
                })
                    .then((res) => {
                        setIsEdit(false)
                        setEditId('')
                        setTitle('');
                        setBody('');
                        getData();
                    })
                    .catch((err) => {
                        console.log(err)
                    })
            } else {
                Axios.post("http://localhost:3001/posts", {
                    title: title,
                    body: body
                })
                    .then((res) => {
                        getData();
                        setTitle('');
                        setBody('');
                    })
                    .catch((err) => {
                        console.log(err)
                    })
            }

        }
    }

    const check = () => {
        setValid({
            titleErr: '',
            bodyErr: ''
        })
    }
    const formValidation = () => {
        // const titleErr = {}
        // const bodyErr = {}
        if (title.trim().length == 0 && body.trim().length == 0) {
            check();
            setErr({
                bodyErr: "empty body",
                titleErr: "empty title"
            })
            // titleErr.emptyTitle = "empty title"
            setValid(false)
        } else if (title.trim().length == 0) {
            check();
            setErr({
                bodyErr: "empty body",
                titleErr: "empty title"
            })
            // titleErr.emptyTitle = "empty title"
            setValid(false)
        }
        else if (body.trim().length == 0) {
            check();
            setErr({
                ...err,
                bodyErr: "empty body"
            })
            // bodyErr.emptyBody = "empty description"
            setValid(false)
        }
        // setTitleErr(titleErr)
        // setBodyErr(bodyErr)


    }

    //delete
    const deleteHandler = (item) => {
        console.log(item, "item")
        Axios.delete(`http://localhost:3001/posts/${item}`)
            .then((res) => {
                getData();
            })
            .catch((err) => {
                console.log(err)
            })
    };
    //edit//update
    const editHandler = (item) => {
        setTitle(item.title);
        setBody(item.body);
        setEditId(item.id)
        setIsEdit(true)
    }

    //error and loading state
    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <>
                <Header />
                <div style={{ textAlign: 'center' }}>
                    <form onSubmit={submitHandler}>
                        <label for="title">Enter Title:</label><br />
                        <input type="text" onChange={(e) => setTitle(e.target.value)} value={title} id="title" name="title" /><br />
                        {/* {Object.keys(titleErr).map((key) => {
                            return <div style={{ color: "red" }}>{titleErr[key]}</div>
                        })} */}
                        {
                            valid ? "" : <div style={{ color: "red" }}>{err.titleErr}</div>
                        }
                        <label for="body">Enter Description:</label><br />
                        <input type="text" onChange={(e) => setBody(e.target.value)} value={body} id="body" name="body" /><br />
                        {/* {Object.keys(bodyErr).map((key) => {
                            return <div style={{ color: "red" }}>{bodyErr[key]}</div>
                        })} */}
                        {
                            valid ? "" : <div style={{ color: "red" }}>{err.bodyErr}</div>
                        }
                        {
                            isEdit ?
                                <button type="submit" >Update</button>
                                :
                                <button type="submit" >Submit</button>
                        }
                    </form>
                </div>
                <ul>
                    {items.map((item, index) => {
                        return (
                            <li key={item.id}>
                                <ul>
                                    <li>ID :- {index + 1}</li>
                                    <li>TITLE :- {item.title}</li>
                                    <li>DESCRIPTION :- {item.body}</li>
                                    <button onClick={() => { deleteHandler(item.id) }} ><i class="material-icons">delete</i></button>
                                    <button onClick={() => { editHandler(item) }}><i class="material-icons">EDIT</i></button>
                                </ul>
                            </li>
                        )
                    })}
                </ul>
            </>
        );
    }
}
export default Posts
